mapper.update:
update
  goods_${storeId}
set
  name = #{name}, code = #{name}, bar_code = #{barCode}, type_id = #{typeId}, brand_id = #{brandId}, unit_id = #{unitId}, label_id = #{labelId}, retail_price = #{retailPrice}, trade_price = #{tradePrice}, purchase_price = #{purchasePrice}, vip_price = #{vipPrice}, inventory = #{inventory}, origin = #{origin}, image = #{image}, oder_type = #{oderType}, degree = #{degree}, net_content = #{netContent}, integral = #{integral}, remark = #{remark}, putaway = #{putaway}
where
  id = #{id};

mapper.select:
select g.id, g.name, g.code, g.bar_code, g.retail_price, g.trade_price, g.purchase_price, g.vip_price, g.inventory, g.origin, g.image, g.oder_type, g.degree, g.net_content, g.integral, g.remark, g.putaway, gb.id as brand_id, gb.name as brand_name, gl.id label_id, gl.name as label_name, gt.id as type_id, gt.name as type_name, gu.id as unit_id, gu.name as unit_name from goods_${goodsVo.storeId} as g, goods_brand_${goodsVo.storeId} as gb, goods_label_${goodsVo.storeId} as gl, goods_type_${goodsVo.storeId} as gt, goods_unit_${goodsVo.storeId} as gu where g.brand_id = gb.id and g.label_id = gl.id and g.type_id = gt.id and g.unit_id = gu.id
and g.type_id = #{goodsVo.typeId}
and g.brand_id = #{goodsVo.brandId}
and g.code = #{goodsVo.code}
and g.bar_code = #{goodsVo.barCode}
and g.putaway = #{goodsVo.putaway}
limit #{pageVo.page}, #{pageVo.pageSize};
