mapper.update:
update
    apply_order_goods_sku_${storeId}
set
    finish_quantity = finish_quantity + #{changeQuantity},
    not_finish_quantity = not_finish_quantity - #{changeQuantity}
where
    id = #{id}
and
    #{changeQuantity} <= not_finish_quantity
and
    #{changeQuantity} + finish_quantity <= quantity;
