mapper.select:
select count(0) from membership_number where 1 = 1
and number like concat('%', #{number}, '%')
and disabled = #{disabled}

select id, number, disabled from membership_number where 1 = 1
and number like concat('%', #{membershipNumber.number}, '%')
and disabled = #{membershipNumber.disabled}
limit #{pageVo.start}, #{pageVo.pageSize};

select id, name, price_type, price from client_level where 1 = 1

    and  id = #{id}


   and  name = #{name}

select count(0) from client as c left join client_level as cl on c.level_id = cl.id where c.level_id > 0
and c.id = #{id}
and c.name like concat('%', #{name}, '%')
and c.phone like concat('%', #{phone}, '%')
and c.membership_number like concat('%', #{membershipNumber}, '%')
and c.level_id = #{levelId}
and c.disabled = #{disabled}

select c.id, c.name, c.username, c.password, c.phone, c.level_id, c.birthday, c.inviter_id, c.address, c.postcode, c.membership_number, c.last_deal_time, c.create_time, c.disabled, c.remark, cl.id as cl_id, cl.name as cl_name, cl.price_type as cl_price_type, cl.price as cl_price from client as c left join client_level as cl on c.level_id = cl.id where c.level_id > 0
and c.id = #{clientVo.id}
and c.name like concat('%', #{clientVo.name}, '%')
and c.phone like concat('%', #{clientVo.phone}, '%')
and c.membership_number like concat('%', #{clientVo.membershipNumber}, '%')
and c.level_id = #{clientVo.levelId}
and c.disabled = #{clientVo.disabled}
limit #{pageVo.start}, #{pageVo.pageSize};

select c.id, c.name, c.username, c.password, c.phone, c.level_id, c.birthday, c.inviter_id, c.address, c.postcode, c.membership_number, c.last_deal_time, c.create_time, c.disabled, c.remark, cl.id as cl_id, cl.name as cl_name, cl.price_type as cl_price_type, cl.price as cl_price from client as c left join client_level as cl on c.level_id = cl.id where c.level_id > 0
and c.id = #{id}
and c.name like concat('%', #{name}, '%')
and c.phone like concat('%', #{phone}, '%')
and c.membership_number like concat('%', #{membershipNumber}, '%')
and c.level_id = #{levelId}
and c.disabled = #{disabled}
and c.inviter_id = #{inviterId}

select id, name, username, password, phone, level_id, birthday, inviter_id, address, postcode, membership_number, last_deal_time, create_time, disabled, remark from client where level_id > 0 and disabled = 0
and id = #{id}
and phone = #{phone}

select count(0) from store_client as sc inner join store as s on s.id = sc.store_id inner join client as c on c.id = sc.client_id where c.level_id > 0 and c.disabled = 0
and sc.store_id = #{storeId}
and sc.client_id = #{clientId}
and c.name like ('%', #{clientName}, '%')

select sc.id, sc.store_id, s.name as store_name, sc.client_id, c.name as client_name, sc.integral, sc.advance_money, sc.push_money from store_client as sc inner join store as s on s.id = sc.store_id inner join client as c on c.id = sc.client_id where c.level_id > 0 and c.disabled = 0
and sc.store_id = #{vo.storeId}
and sc.client_id = #{vo.clientId}
and c.name like ('%', #{vo.clientName}, '%')
limit #{pageVo.start}, #{pageVo.pageSize};

mapper.update:
update
    store_client_detail
set
    update_time = now(), status = #{status}, user_id = #{userId}, remark = #{remark}
where
    id = #{id}
and status = 0;

mapper.select:
select count(0) from store_client_detail as scd inner join store as s on s.id = scd.store_id inner join client as c on c.id = scd.client_id
left join user_${storeId} as u on u.id = scd.user_id
where c.level_id > 0 and c.disabled = 0
and scd.store_id = #{storeId}
and scd.client_id = #{clientId}
and scd.type = #{type}
and scd.status = #{status}
and c.name like concat('%', #{clientName}, '%')
order by scd.create_time desc;

select scd.id, scd.store_id, s.name as store_name, scd.client_id, c.name as client_name, scd.create_time, scd.update_time, scd.type, if (scd.type = 1, '积分增加', if (scd.type = 2, '积分减少', if (scd.type = 3, '提成增加', '提成减少'))) as type_name, scd.change_integral, scd.change_push_money, scd.order_id, scd.status, if (scd.status = 0, '待审核', if (scd.status = 1, '审核通过', '审核未通过')) as status_name, scd.user_id,
u.name as user_name,
scd.withdrawal_way, if (scd.withdrawal_way = 1, '提现到客户手机号码对应的支付宝账户', '其他方式') as withdrawal_way_name, scd.remark from store_client_detail as scd inner join store as s on s.id = scd.store_id inner join client as c on c.id = scd.client_id
left join user_${vo.storeId} as u on u.id = scd.user_id
where c.level_id > 0 and c.disabled = 0
and scd.store_id = #{vo.storeId}
and scd.client_id = #{vo.clientId}
and scd.type = #{vo.type}
and scd.status = #{vo.status}
and c.name like concat('%', #{vo.clientName}, '%')
order by scd.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};
