mapper.select:
select id, number from client_membership_number where 1 = 1
and number = #{clientMembershipNumber.number}
limit #{pageVo.start}, #{pageVo.pageSize};

select count(0) from client as c, client_level as cl, client_client_level as ccl where c.id = ccl.client_id and cl.id = ccl.level_id
and c.id = #{id}
and c.name = #{name}
and c.phone = #{phone}
and c.membership_number = #{membershipNumber}
and cl.id = #{levelId}

select c.id, c.name, c.username, c.password, c.phone, c.birthday, c.inviter_id, c.inviter_name, c.integral, c.address, c.postcode, c.membership_number, c.last_deal_time, c.create_time, c.disabled, c.remark, cl.id as levelId, cl.name as levelName from client as c, client_level as cl, client_client_level as ccl where c.id = ccl.client_id and cl.id = ccl.level_id
and c.id = #{clientVo.id}
and c.name = #{clientVo.name}
and c.phone = #{clientVo.phone}
and c.membership_number = #{clientVo.membershipNumber}
and cl.id = #{clientVo.levelId}
limit #{pageVo.start}, #{pageVo.pageSize};

select c.id, c.name, c.username, c.password, c.phone, c.birthday, c.inviter_id, c.inviter_name, c.integral, c.address, c.postcode, c.membership_number, c.last_deal_time, c.create_time, c.disabled, c.remark, cl.id as levelId, cl.name as levelName from client as c, client_level as cl, client_client_level as ccl where c.id = ccl.client_id and cl.id = ccl.level_id
and c.id = #{id}
and c.phone = #{phone}
and c.membership_number = #{membershipNumber}
