mapper.select:
select count(0) from ( ( ( fund_order_1 as fo left join procurement_apply_order_1 as pao on fo.apply_order_id = pao.id ) left join user_1 as u on fo.user_id = u.id ) left join bank_account_1 as ba on fo.bank_account_id = ba.id ) left join supplier_1 as s on pao.supplier_id = s.id where fo.type = #{type}
and fo.id = #{id}
and s.name like concat('%', #{procurementApplyOrderVo.supplierName}, '%')
and date(fo.create_time) between #{procurementApplyOrderVo.startTime} and #{procurementApplyOrderVo.endTime}
order by fo.create_time desc;

select fo.id, fo.type, fo.create_time, fo.apply_order_id, fo.order_status, ba.name as ba_name, ba.account as ba_account, fo.money, u.name as user_name, fo.remark, s.name as supplier_name from ( ( ( fund_order_1 as fo left join procurement_apply_order_1 as pao on fo.apply_order_id = pao.id ) left join user_1 as u on fo.user_id = u.id ) left join bank_account_1 as ba on fo.bank_account_id = ba.id ) left join supplier_1 as s on pao.supplier_id = s.id where fo.type = #{fundOrderVo.type}
and fo.id = #{fundOrderVo.id}
and s.name like concat('%', #{fundOrderVo.procurementApplyOrderVo.supplierName}, '%')
and date(fo.create_time) between #{fundOrderVo.procurementApplyOrderVo.startTime} and #{fundOrderVo.procurementApplyOrderVo.endTime}
order by fo.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};
