mapper.select:
select count(0) from ( ( ( ( ( fund_order_${storeId} as fo left join procurement_apply_order_${storeId} as pao on fo.apply_order_id = pao.id ) left join user_${storeId} as u on fo.user_id = u.id ) left join bank_account_${storeId} as ba on fo.bank_account_id = ba.id ) left join supplier_${storeId} as s on pao.supplier_id = s.id ) left join sell_apply_order_${storeId} as sao on fo.apply_order_id = sao.id ) left join client as c on sao.client_id = c.id where fo.type = #{type}
and fo.id = #{id}
and s.name like concat('%', #{procurementApplyOrderVo.supplierName}, '%')
and date(fo.create_time) between #{procurementApplyOrderVo.startTime} and #{procurementApplyOrderVo.endTime}
and c.name like concat('%', #{sellApplyOrderVo.client.name}, '%')
and c.phone like concat('%', #{sellApplyOrderVo.client.phone}, '%')
and c.membershipNumber like concat('%', #{sellApplyOrderVo.client.membershipNumber}, '%')
and date(fo.create_time) between #{sellApplyOrderVo.startTime} and #{sellApplyOrderVo.endTime}
order by fo.create_time desc;

select fo.id, fo.type, fo.create_time, fo.apply_order_id, fo.order_status, ba.name as ba_name, ba.account as ba_account, fo.money, u.name as user_name, fo.remark, s.name as supplier_name, c.name as client_name, c.phone as client_phone, c.membership_number as client_membership_number from ( ( ( ( ( fund_order_${fundOrderVo.storeId} as fo left join procurement_apply_order_${fundOrderVo.storeId} as pao on fo.apply_order_id = pao.id ) left join user_${fundOrderVo.storeId} as u on fo.user_id = u.id ) left join bank_account_${fundOrderVo.storeId} as ba on fo.bank_account_id = ba.id ) left join supplier_${fundOrderVo.storeId} as s on pao.supplier_id = s.id ) left join sell_apply_order_${fundOrderVo.storeId} as sao on fo.apply_order_id = sao.id ) left join client as c on sao.client_id = c.id where fo.type = #{fundOrderVo.type}
and fo.id = #{fundOrderVo.id}
and s.name like concat('%', #{fundOrderVo.procurementApplyOrderVo.supplierName}, '%')
and date(fo.create_time) between #{fundOrderVo.procurementApplyOrderVo.startTime} and #{fundOrderVo.procurementApplyOrderVo.endTime}
and c.name like concat('%', #{fundOrderVo.sellApplyOrderVo.client.name}, '%')
and c.phone like concat('%', #{fundOrderVo.sellApplyOrderVo.client.phone}, '%')
and c.membershipNumber like concat('%', #{fundOrderVo.sellApplyOrderVo.client.membershipNumber}, '%')
and date(fo.create_time) between #{fundOrderVo.sellApplyOrderVo.startTime} and #{fundOrderVo.sellApplyOrderVo.endTime}
order by fo.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};
