mapper.update:
update
    procurement_apply_order_${storeId}
set
    in_total_quantity = #{inTotalQuantity}, total_money = #{totalMoney}, total_discount_money = #{totalDiscountMoney}, order_money = #{orderMoney}
where
    id = #{id};

update
    procurement_apply_order_${storeId}
set
    order_status = #{orderStatus}

    , in_received_quantity = in_received_quantity + #{quantity}, in_not_received_quantity = in_not_received_quantity - #{quantity}


    , out_sent_quantity = out_sent_quantity + #{quantity}, out_not_sent_quantity = out_not_sent_quantity - #{quantity}

where
    id = #{applyOrderId}

update
    procurement_apply_order_${storeId}
set
    clear_status = #{orderStatus},
    cleared_money = cleared_money + #{money} + #{discountMoney} + #{advanceMoney},
    not_cleared_money = not_cleared_money - #{money} - #{discountMoney} - #{advanceMoney}
where
    id = #{orderId}

mapper.select:
select
    count(0)
from
    (
        procurement_apply_order_${storeId} as pao
        left join supplier_${storeId} as s on pao.supplier_id = s.id
    )
    left join user_${storeId} as u on pao.user_id = u.id
where pao.type = #{type}

    and pao.id = #{id}


    and s.name like concat('%', #{supplierName}, '%')


    and date(pao.create_time) between #{startTime} and #{endTime}

select pao.id, pao.type, pao.create_time, pao.result_order_id, pao.order_status, pao.clear_status, s.id as supplier_id, s.name as supplier_name, pao.in_warehouse_id, pao.in_total_quantity, pao.in_received_quantity, pao.in_not_received_quantity, pao.out_warehouse_id, pao.out_total_quantity, pao.out_sent_quantity, pao.out_not_sent_quantity, pao.total_money, pao.total_discount_money, pao.order_money, pao.cleared_money, pao.not_cleared_money, u.name as user_name, pao.remark from ( procurement_apply_order_${procurementApplyOrderVo.storeId} as pao left join supplier_${procurementApplyOrderVo.storeId} as s on pao.supplier_id = s.id ) left join user_${procurementApplyOrderVo.storeId} as u on pao.user_id = u.id where pao.type = #{procurementApplyOrderVo.type}
and pao.id = #{procurementApplyOrderVo.id}
and s.name like concat('%', #{procurementApplyOrderVo.supplierName}, '%')
and date(pao.create_time) between #{procurementApplyOrderVo.startTime} and #{procurementApplyOrderVo.endTime}
order by pao.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};

select
    id, type, create_time, result_order_id, order_status, clear_status, supplier_id, in_warehouse_id, in_total_quantity, in_received_quantity, in_not_received_quantity, out_warehouse_id, out_total_quantity, out_sent_quantity, out_not_sent_quantity, total_money, total_discount_money, order_money, cleared_money, not_cleared_money, user_id, remark
from
    procurement_apply_order_${storeId}
where
    id = #{id};

select pao.id, pao.type, pao.create_time, pao.result_order_id, pao.order_status, pao.clear_status, s.id as supplier_id, s.name as supplier_name, pao.in_warehouse_id, pao.in_total_quantity, pao.in_received_quantity, pao.in_not_received_quantity, pao.out_warehouse_id, pao.out_total_quantity, pao.out_sent_quantity, pao.out_not_sent_quantity, pao.total_money, pao.total_discount_money, pao.order_money, pao.cleared_money, pao.not_cleared_money, u.name as user_name, pao.remark, ogs.id as ogs_id, ogs.type as ogs_type, ogs.goods_sku_id as ogs_goods_sku_id, ogs.quantity as ogs_quantity, ogs.finish_quantity as ogs_finish_quantity, ogs.not_finish_quantity as ogs_not_finish_quantity, ogs.money as ogs_money, ogs.discount_money as ogs_discount_money, ogs.remark as ogs_remark, g.name as g_name, g.id as g_id, g.bar_code as g_bar_code, gs.sku as gs_sku, gs.purchase_price as gs_purchase_price from ( ( ( ( procurement_apply_order_${storeId} as pao left join supplier_${storeId} as s on pao.supplier_id = s.id ) left join user_${storeId} as u on pao.user_id = u.id ) left join order_goods_sku_${storeId} as ogs on pao.id = ogs.order_id ) left join goods_sku_${storeId} as gs on ogs.goods_sku_id = gs.id ) left join goods_${storeId} as g on gs.goods_id = g.id where 1 = 1
and pao.id = #{id}
and pao.supplier_id = #{supplierId}
and pao.user_id = #{userId}

select count(0) from ( ( procurement_result_order_${storeId} as pro left join procurement_apply_order_${storeId} as pao on pro.apply_order_id = pao.id ) left join supplier_${storeId} as s on pao.supplier_id = s.id ) left join user_${storeId} as u on pao.user_id = u.id where 1 = 1
and pro.id = #{id}
and pro.type = #{type}
and s.name like concat('%', #{procurementApplyOrderVo.supplierName}, '%')
and date(pro.create_time) between #{procurementApplyOrderVo.startTime} and #{procurementApplyOrderVo.endTime}
order by pro.create_time desc;

select pro.id, pro.type, pro.create_time, pro.apply_order_id, pro.order_status, pro.total_quantity, pro.total_money, pro.total_discount_money, pro.order_money, u.name as user_name, pro.remark, pao.clear_status as pao_clear_status, s.id as supplier_id, s.name as supplier_name, pao.in_warehouse_id, pao.out_warehouse_id from ( ( procurement_result_order_${procurementResultOrderVo.storeId} as pro left join procurement_apply_order_${procurementResultOrderVo.storeId} as pao on pro.apply_order_id = pao.id ) left join supplier_${procurementResultOrderVo.storeId} as s on pao.supplier_id = s.id ) left join user_${procurementResultOrderVo.storeId} as u on pro.user_id = u.id where 1 = 1
and pro.id = #{procurementResultOrderVo.id}
and pro.type = #{procurementResultOrderVo.type}
and s.name like concat('%', #{procurementResultOrderVo.procurementApplyOrderVo.supplierName}, '%')
and date(pro.create_time) between #{procurementResultOrderVo.procurementApplyOrderVo.startTime} and #{procurementResultOrderVo.procurementApplyOrderVo.endTime}
order by pro.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};
