mapper.update:
update
    sell_apply_order_${storeId}
set
    out_total_quantity = #{outTotalQuantity}, total_money = #{totalMoney}, discount_money = #{discountMoney}, discount_coupon_id = #{discountCouponId}, total_discount_money = #{totalDiscountMoney}, order_money = #{orderMoney}
where
    id = #{id};

update
    sell_apply_order_${storeId}
set
    order_status = #{orderStatus}

    , in_received_quantity = in_received_quantity + #{quantity}, in_not_received_quantity = in_not_received_quantity - #{quantity}


    , out_sent_quantity = out_sent_quantity + #{quantity}, out_not_sent_quantity = out_not_sent_quantity - #{quantity}

where
    id = #{applyOrderId}

update
    sell_apply_order_${storeId}
set
    clear_status = #{orderStatus},
    cleared_money = cleared_money + #{money} + #{discountMoney} + #{advanceMoney},
    not_cleared_money = not_cleared_money - #{money} - #{discountMoney} - #{advanceMoney}
where
    id = #{orderId}

mapper.select:
select count(0) from ( sell_apply_order_${storeId} as sao left join client as c on sao.client_id = c.id ) left join user_${storeId} as u on sao.user_id = u.id where sao.type = #{type}
and sao.id = #{id}
and c.name like concat('%', #{client.name}, '%')
and c.phone like concat('%', #{client.phone}, '%')
and c.membership_number like concat('%', #{client.membershipNumber}, '%')
and date(sao.create_time) between #{startTime} and #{endTime}

select sao.id, sao.type, sao.create_time, sao.result_order_id, sao.prodcing_way, sao.order_status, sao.clear_status, c.id as client_id, c.name as client_name, c.phone as client_phone, c.membership_number as client_membership_number, sao.in_warehouse_id, sao.in_total_quantity, sao.in_received_quantity, sao.in_not_received_quantity, sao.out_warehouse_id, sao.out_total_quantity, sao.out_sent_quantity, sao.out_not_sent_quantity, sao.total_money, sao.discount_money, sao.discount_coupon_id, sao.total_discount_money, sao.order_money, sao.cleared_money, sao.not_cleared_money, u.name as user_name, sao.remark from ( sell_apply_order_${sellApplyOrderVo.storeId} as sao left join client as c on sao.client_id = c.id ) left join user_${sellApplyOrderVo.storeId} as u on sao.user_id = u.id where sao.type = #{sellApplyOrderVo.type}
and sao.id = #{sellApplyOrderVo.id}
and c.name like concat('%', #{sellApplyOrderVo.client.name}, '%')
and c.phone like concat('%', #{sellApplyOrderVo.client.phone}, '%')
and c.membership_number like concat('%', #{sellApplyOrderVo.client.membershipNumber}, '%')
and date(sao.create_time) between #{sellApplyOrderVo.startTime} and #{sellApplyOrderVo.endTime}
order by sao.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};

select
    id, type, create_time, result_order_id, prodcing_way, order_status, clear_status, client_id, in_warehouse_id, in_total_quantity, in_received_quantity, in_not_received_quantity, out_warehouse_id, out_total_quantity, out_sent_quantity, out_not_sent_quantity, total_money, discount_money, discount_coupon_id, total_discount_money, order_money, cleared_money, not_cleared_money, user_id, remark
from
    sell_apply_order_${storeId}
where
    id = #{id};

select count(0) from ( ( sell_result_order_${storeId} as sro left join sell_apply_order_${storeId} as sao on sro.apply_order_id = sao.id ) left join client as c on sao.client_id = c.id ) left join user_${storeId} as u on sro.user_id = u.id where 1 = 1
and sro.id = #{id}
and sro.type = #{type}
and c.name like concat('%', #{sellApplyOrderVo.client.name}, '%')
and c.phone like concat('%', #{sellApplyOrderVo.client.phone}, '%')
and c.membershipNumber like concat('%', #{sellApplyOrderVo.client.membershipNumber}, '%')
and date(sro.create_time) between #{sellApplyOrderVo.startTime} and #{sellApplyOrderVo.endTime}
order by sro.create_time desc;

select sro.id, sro.type, sro.create_time, sro.apply_order_id, sro.order_status, sro.total_quantity, sro.total_money, sro.total_discount_money, sro.order_money, sro.cost_money, sro.gross_margin_money, u.name as user_name, sro.remark, sao.clear_status as sao_clear_status, sao.in_warehouse_id, sao.out_warehouse_id, c.name as client_name, c.phone as client_phone, c.membership_number as client_membership_number from ( ( sell_result_order_${sellResultOrderVo.storeId} as sro left join sell_apply_order_${sellResultOrderVo.storeId} as sao on sro.apply_order_id = sao.id ) left join client as c on sao.client_id = c.id ) left join user_${sellResultOrderVo.storeId} as u on sro.user_id = u.id where 1 = 1
and sro.id = #{sellResultOrderVo.id}
and sro.type = #{sellResultOrderVo.type}
and c.name like concat('%', #{sellResultOrderVo.sellApplyOrderVo.client.name}, '%')
and c.phone like concat('%', #{sellResultOrderVo.sellApplyOrderVo.client.phone}, '%')
and c.membershipNumber like concat('%', #{sellResultOrderVo.sellApplyOrderVo.client.membershipNumber}, '%')
and date(sro.create_time) between #{sellResultOrderVo.sellApplyOrderVo.startTime} and #{sellResultOrderVo.sellApplyOrderVo.endTime}
order by sro.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};
