mapper.select:
select count(0) from ( ( storage_order_${storeId} as so left join procurement_apply_order_${storeId} as pao on so.apply_order_id = pao.id ) left join supplier_${storeId} as s on pao.supplier_id = s.id ) left join user_${storeId} as u on so.user_id = u.id where so.type = #{type}
and pro.id = #{id}
and s.name like concat('%', #{procurementApplyOrderVo.supplierName}, '%')
and date(so.create_time) between #{procurementApplyOrderVo.startTime} and #{procurementApplyOrderVo.endTime}
order by so.create_time desc

select so.id, so.type, so.create_time, so.apply_order_id, so.order_status, s.name as supplier_name, pao.in_warehouse_id, pao.out_warehouse_id, so.quantity, so.logistics_company, so.waybill_number, u.name as user_name, so.remark as remark from ( ( storage_order_${storageOrderVo.storeId} as so left join procurement_apply_order_${storageOrderVo.storeId} as pao on so.apply_order_id = pao.id ) left join supplier_${storageOrderVo.storeId} as s on pao.supplier_id = s.id ) left join user_${storageOrderVo.storeId} as u on so.user_id = u.id where so.type = #{storageOrderVo.type}
and pro.id = #{storageOrderVo.id}
and s.name like concat('%', #{storageOrderVo.procurementApplyOrderVo.supplierName}, '%')
and date(so.create_time) between #{storageOrderVo.procurementApplyOrderVo.startTime} and #{storageOrderVo.procurementApplyOrderVo.endTime}
order by so.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};

select
    count(0)
from
    (
        (
            (
                storage_result_order_${storeId} as sro
                left join warehouse_${storeId} as w on sro.warehouse_id = w.id
            )
            left join user_${storeId} as u on sro.user_id = u.id
        )
        left join supplier_${storeId} as s on sro.target_id = s.id
    )
left join client as c on sro.target_id = c.id
where 1 = 1

    and sro.id = #{id}


    and sro.type = #{type}


    and (c.name like concat('%', #{targetName}, '%') or s.name like concat('%', #{targetName}, '%'))


    and date(sro.create_time) between #{startTime} and #{endTime}

order by sro.create_time desc;

select sro.id, sro.type, sro.create_time, sro.order_status, sro.target_type, sro.target_id, c.name as client_name, s.name as supplier_name, sro.warehouse_id, w.name as warehouse_name, sro.total_quantity, sro.total_money, sro.user_id, u.name as u_name, sro.remark from ( ( ( storage_result_order_${vo.storeId} as sro left join warehouse_${vo.storeId} as w on sro.warehouse_id = w.id ) left join user_${vo.storeId} as u on sro.user_id = u.id ) left join supplier_${vo.storeId} as s on sro.target_id = s.id ) left join client as c on sro.target_id = c.id where 1 = 1
and sro.id = #{vo.id}
and sro.type = #{vo.type}
and (c.name like concat('%', #{vo.targetName}, '%') or s.name like concat('%', #{vo.targetName}, '%'))
and date(sro.create_time) between #{vo.startTime} and #{vo.endTime}
order by sro.create_time desc limit #{pageVo.start}, #{pageVo.pageSize};

select count(*) from ( select count(*) from ( ( ( goods_${storeId} as g left join goods_sku_${storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{id}, '%')
and g.name like concat('%', #{name}, '%')
and g.type_id = #{typeId}
group by g.id ) as temp;

select g.id, g.name, g.bar_code, gt.name as type_name, g.image, sum(wgs.real_inventory) as real_inventory, sum(wgs.not_sent_quantity) as not_sent_quantity, sum(wgs.not_received_quantity) as not_received_quantity, sum(wgs.can_use_inventory) as can_use_inventory, sum(wgs.book_inventory) as book_inventory from ( ( ( goods_${vo.storeId} as g left join goods_sku_${vo.storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${vo.storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${vo.storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${vo.storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{vo.id}, '%')
and g.name like concat('%', #{vo.name}, '%')
and g.type_id = #{vo.typeId}
group by g.id limit #{pageVo.start}, #{pageVo.pageSize};

select
    count(*)
from
    (
        (
            select
                pro.id as id,
                if (pro.type = 1, '采购入库单', if (pro.type = 2, '采购退货单', if (pro.type = 3, '采购换货单', null))) as type_name,
                pro.create_time as create_time,
                pro.apply_order_id as apply_order_id,
                s.name as target_name,
                gs.sku as sku,
                if (ogs.type = 1, pao.in_warehouse_id, null) as in_warehouse_id,
                if (ogs.type = 1, ogs.quantity, null) as in_total_quantity,
                if (ogs.type = 1, ogs.money, null) as in_total_money,
                if (ogs.type = 1, ogs.discount_money, null) as in_total_discount_money,
                if (ogs.type = 0, pao.out_warehouse_id, null) as out_warehouse_id,
                if (ogs.type = 0, ogs.quantity, null) as out_total_quantity,
                if (ogs.type = 0, ogs.money, null) as out_total_money,
                if (ogs.type = 0, ogs.discount_money, null) as out_total_discount_money,
                gs.purchase_price * ogs.quantity as total_purchase_price,
                u.name as user_name,
                pro.remark as remark
            from
                (
                    (
                        (
                            (
                                (
                                    procurement_result_order_${storeId} as pro
                                    left join order_goods_sku_${storeId} as ogs on pro.id = ogs.order_id
                                )
                                left join goods_sku_${storeId} as gs on ogs.goods_sku_id = gs.id
                            )
                            left join goods_${storeId} as g on gs.goods_id = g.id
                        )
                        left join procurement_apply_order_${storeId} as pao on pro.apply_order_id = pao.id
                    )
                    left join supplier_${storeId} as s on pao.supplier_id = s.id
                )
            left join user_${storeId} as u on pro.user_id = u.id
            where g.id = #{goodsId}

                and pro.id = #{id}


                and date(pro.create_time) between #{startTime} and #{endTime}

            order by pro.create_time desc
        )
        union all
        (
            select
                sro.id as id,
                if (sro.type = 1, '零售单', if (sro.type = 2, '销售出库单', if (sro.type = 3, '销售退货单', if (sro.type = 4, '销售换货单', null)))) as type_name,
                sro.create_time as create_time,
                sro.apply_order_id as apply_order_id,
                c.name as target_name,
                gs.sku as sku,
                if (ogs.type = 1, sao.in_warehouse_id, null) as in_warehouse_id,
                if (ogs.type = 1, ogs.quantity, null) as in_total_quantity,
                if (ogs.type = 1, ogs.money, null) as in_total_money,
                if (ogs.type = 1, ogs.discount_money, null) as in_total_discount_money,
                if (ogs.type = 0, sao.out_warehouse_id, null) as out_warehouse_id,
                if (ogs.type = 0, ogs.quantity, null) as out_total_quantity,
                if (ogs.type = 0, ogs.money, null) as out_total_money,
                if (ogs.type = 0, ogs.discount_money, null) as out_total_discount_money,
                gs.purchase_price * ogs.quantity as total_purchase_price,
                u.name as user_name,
                sro.remark as remark
            from
                (
                    (
                        (
                            (
                                (
                                    sell_result_order_${storeId} as sro
                                    left join order_goods_sku_${storeId} as ogs on sro.id = ogs.order_id
                                )
                                left join goods_sku_${storeId} as gs on ogs.goods_sku_id = gs.id
                            )
                            left join goods_${storeId} as g on gs.goods_id = g.id
                        )
                        left join sell_apply_order_${storeId} as sao on sro.apply_order_id = sao.id
                    )
                    left join client as c on sao.client_id = c.id
                )
            left join user_${storeId} as u on sro.user_id = u.id
            where g.id = #{goodsId}

                and sro.id = #{id}


                and date(sro.create_time) between #{startTime} and #{endTime}

            order by sro.create_time desc
        )
        union all
        (
            select
                sro.id as id,
                if (sro.type = 1, '其他入库单', if (sro.type = 2, '其他出库单', if (sro.type = 3, '报溢单', if ( sro.type = 4, '报损单', null)))) as type_name,
                sro.create_time as create_time,
                null as apply_order_id,
                c. NAME as target_name,
                gs.sku as sku,
                if (ogs.type = 1, sro.warehouse_id, null) as in_warehouse_id,
                if (ogs.type = 1, ogs.quantity, null) as in_total_quantity,
                if (ogs.type = 1, ogs.money, null) as in_total_money,
                if (ogs.type = 1, ogs.discount_money, null) as in_total_discount_money,
                if (ogs.type = 0, sro.warehouse_id, null) as out_warehouse_id,
                if (ogs.type = 0, ogs.quantity, null) as out_total_quantity,
                if (ogs.type = 0, ogs.money, null) as out_total_money,
                if (ogs.type = 0, ogs.discount_money, null) as out_total_discount_money,
                gs.purchase_price * ogs.quantity as total_purchase_price,
                u. NAME as user_name,
                sro.remark as remark
            from
                (
                    (
                        (
                            (
                                (
                                    storage_result_order_${storeId} as sro
                                    left join order_goods_sku_${storeId} as ogs on sro.id = ogs.order_id
                                )
                                left join goods_sku_${storeId} as gs on ogs.goods_sku_id = gs.id
                            )
                            left join goods_${storeId} as g on gs.goods_id = g.id
                        )
                        left join supplier_${storeId} as s on sro.target_id = s.id
                    )
                    left join client as c on sro.target_id = c.id
                )
            left join user_${storeId} as u on sro.user_id = u.id
            where g.id = #{goodsId}

                and sro.id = #{id}


                and date(sro.create_time) between #{startTime} and #{endTime}

            order by sro.create_time desc
        )
    ) as temp;

