select count(*) from ( select count(*) from ( ( ( goods_${storeId} as g left join goods_sku_${storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{id}, '%')
and g.name like concat('%', #{name}, '%')
and g.type_id = #{typeId}
group by gs.id ) as temp;

SELECT g.id, g.name, g.bar_code, gt.name as type_name, g.image, g.skus, gs.sku, sum(wgs.real_inventory) as real_inventory, sum(wgs.not_sent_quantity) as not_sent_quantity, sum(wgs.not_received_quantity) as not_received_quantitym, sum(wgs.can_use_inventory) as can_use_inventory, sum(wgs.book_inventory) as book_inventory FROM ( ( ( goods_${vo.storeId} as g left join goods_sku_${vo.storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${vo.storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${vo.storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${vo.storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{vo.id}, '%')
and g.name like concat('%', #{vo.name}, '%')
and g.type_id = #{vo.typeId}
group by gs.id limit #{pageVo.start}, #{pageVo.pageSize};

select count(*) from ( ( ( goods_${storeId} as g left join goods_sku_${storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{id}, '%')
and g.name like concat('%', #{name}, '%')
and g.type_id = #{typeId}
and w.id = #{warehouseId}

select g.id, g.name, g.bar_code, gt.name as type_name, g.image, g.skus, gs.id, gs.sku, w.name as warehouse_name, wgs.real_inventory as real_inventory, wgs.not_sent_quantity as not_sent_quantity, wgs.not_received_quantity as not_received_quantity, wgs.can_use_inventory as can_use_inventory, wgs.book_inventory as book_inventory, wgs.inventory_upper_limit, wgs.inventory_low_limit, wgs.inventory_low_limit + wgs.not_sent_quantity - wgs.real_inventory - wgs.not_received_quantity as need_quantity from ( ( ( goods_${vo.storeId} as g left join goods_sku_${vo.storeId} as gs on g.id = gs.goods_id ) left join warehouse_goods_sku_${vo.storeId} as wgs on gs.id = wgs.goods_sku_id ) left join warehouse_${vo.storeId} as w on w.id = wgs.warehouse_id ) left join goods_type_${vo.storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{vo.id}, '%')
and g.name like concat('%', #{vo.name}, '%')
and g.type_id = #{vo.typeId}
and w.id = #{vo.warehouseId}
and wgs.book_inventory >= wgs.inventory_upper_limit or wgs.book_inventory <= wgs.inventory_low_limit
and wgs.inventory_low_limit + wgs.not_sent_quantity - wgs.real_inventory - wgs.not_received_quantity > 0
limit #{pageVo.start}, #{pageVo.pageSize};
