    and date(sco.create_time) between #{startTime} and #{endTime}

order by sco.create_time asc;

select
    sco.order_id,
    if (
        pro.type = 1,
        '采购入库单',
    if (
        pro.type = 2,
        '采购退货单',
    if (
        pro.type = 3,
        '采购换货单',
    if (
        sro.type = 1,
        '零售单',
    if (
        sro.type = 2,
        '销售出库单',
    if (
        sro.type = 3,
        '销售退货单',
    if (
        sro.type = 4,
        '销售换货单',
    if (
        sro1.type = 1,
        '其他入库单',
    if (
        sro1.type = 2,
        '其他出库单',
    if (
        sro1.type = 3,
        '报溢单',
    if (
        sro1.type = 4,
        '报损单',
    if (
        sro1.type = 5,
        '成本调价单',
    null
    )))))))))))) as type_name,
    sco.create_time,
    if (
        pro.apply_order_id is not null,
        pro.apply_order_id,
    if (
        sro.apply_order_id is not null,
        sro.apply_order_id,
        null
    )) as apply_order_id,
    if (
        c.name is not null,
        c.name,
        s.name
    ) as target_name,
    gs.sku as sku,
    sco.warehouse_id,
    w.name as warehouse_name,
    sco.in_quantity,
    sco.in_money,
    sco.in_total_money,
    sco.out_quantity,
    sco.out_money,
    sco.out_total_money,
    sco.check_quantity1,
    sco.check_money1,
    sco.check_total_money1,
    u.name as user_name,
    if (
        pro.remark is not null,
        pro.remark,
    if (
        sro.remark is not null,
        sro.remark,
    if (
        sro1.remark is not null,
        sro1.remark,
        null
    ))) as remark
from
    (
        (
            (
                (
                    (
                        (
                            (
                                (
                                    storage_check_order_${vo.storeId} as sco
                                    left join procurement_result_order_${vo.storeId} as pro on pro.id = sco.order_id
                                )
                                left join sell_result_order_${vo.storeId} as sro on sro.id = sco.order_id
                            )
                            left join storage_result_order_${vo.storeId} as sro1 on sro1.id = sco.order_id
                        )
                        left join goods_sku_${vo.storeId} as gs on gs.id = sco.goods_sku_id
                    )
                    left join goods_${vo.storeId} as g on g.id = gs.goods_id
                )
                left join supplier_${vo.storeId} as s on s.id = sco.target_id
            )
            left join client as c on c.id = sco.target_id
        )
        left join user_${vo.storeId} as u on u.id = sco.user_id
    )
left join warehouse_${vo.storeId} as w on w.id = sco.warehouse_id
where gs.id = #{vo.goodsSkuId}

    and date(sco.create_time) between #{vo.startTime} and #{vo.endTime}

order by sco.create_time asc
limit #{pageVo.start}, #{pageVo.pageSize};

select g.id, g.name, g.bar_code, gt.name as type_name, g.image, gs.id as goods_sku_id, gs.sku, gs.goods_id, gs.retail_price, gs.vip_price, w.id as warehouse_id, w.name as warehouse_name, wgs.real_inventory, wgs.not_sent_quantity, wgs.not_received_quantity, wgs.can_use_inventory, wgs.book_inventory, wgs.inventory_upper_limit, wgs.inventory_low_limit, wgs.inventory_low_limit + wgs.not_sent_quantity - wgs.real_inventory - wgs.not_received_quantity as need_quantity, wgs.opening_quantity, wgs.opening_money, wgs.opening_total_money from ( ( ( goods_${vo.storeId} as g inner join goods_sku_${vo.storeId} as gs on g.id = gs.goods_id ) inner join warehouse_goods_sku_${vo.storeId} as wgs on gs.id = wgs.goods_sku_id ) inner join warehouse_${vo.storeId} as w on w.id = wgs.warehouse_id ) inner join goods_type_${vo.storeId} as gt on g.type_id = gt.id where 1 = 1
and g.id like concat('%', #{vo.id}, '%')
and g.name like concat('%', #{vo.name}, '%')
and g.type_id = #{vo.typeId}
and w.id = #{vo.warehouseId}
and wgs.book_inventory >= wgs.inventory_upper_limit or wgs.book_inventory <= wgs.inventory_low_limit
and wgs.inventory_low_limit + wgs.not_sent_quantity - wgs.real_inventory - wgs.not_received_quantity > 0
and wgs.opening_money > 0
limit #{pageVo.start}, #{pageVo.pageSize};

