        sro.type = 4,
        '销售换货单',
    if (
        sro1.type = 1,
        '其他入库单',
    if (
        sro1.type = 2,
        '其他出库单',
    if (
        sro1.type = 3,
        '报溢单',
    if (
        sro1.type = 4,
        '报损单',
    if (
        sro1.type = 5,
        '成本调价单',
        null
    )))))))))))) as type_name,
    sco.create_time,
    if (
        pro.apply_order_id is not null,
        pro.apply_order_id,
    if (
        sro.apply_order_id is not null,
        sro.apply_order_id,
        null
    )) as apply_order_id,
    if (
        c.name is not null,
        c.name,
        s.name
    ) as target_name,
    gs.sku as sku,
    sco.warehouse_id,
    w.name as warehouse_name,
    sco.in_quantity,
    sco.in_money,
    sco.in_total_money,
    sco.out_quantity,
    sco.out_money,
    sco.out_total_money,
    sco.check_quantity,
    sco.check_money,
    sco.check_total_money,
    u.name as user_name,
    if (
        pro.remark is not null,
        pro.remark,
    if (
        sro.remark is not null,
        sro.remark,
    if (
        sro1.remark is not null,
        sro1.remark,
    null
    ))) as remark
from
    (
        (
            (
                (
                    (
                        (
                            (
                                (
                                    storage_check_order_${vo.storeId} as sco
                                    left join procurement_result_order_${vo.storeId} as pro on pro.id = sco.order_id
                                )
                                left join sell_result_order_${vo.storeId} as sro on sro.id = sco.order_id
                            )
                            left join storage_result_order_${vo.storeId} as sro1 on sro1.id = sco.order_id
                        )
                        left join goods_sku_${vo.storeId} as gs on gs.id = sco.goods_sku_id
                    )
                    left join goods_${vo.storeId} as g on g.id = gs.goods_id
                )
                left join supplier_${vo.storeId} as s on s.id = sco.target_id
            )
            left join client as c on c.id = sco.target_id
        )
        left join user_${vo.storeId} as u on u.id = sco.user_id
    )
left join warehouse_${vo.storeId} as w on w.id = sco.warehouse_id
where gs.id = #{vo.goodsSkuId}
and w.id = #{vo.warehouseId}

    and date(sco.create_time) between #{vo.startTime} and #{vo.endTime}

order by sco.create_time asc
limit #{pageVo.start}, #{pageVo.pageSize};

mapper.insert:
insert into
    storage_check_order_${storeId} (order_id, create_time, order_status, target_id, goods_id, goods_sku_id, warehouse_id, in_quantity, in_money, in_total_money, out_quantity, out_money, out_total_money, check_quantity, check_money, check_total_money, check_quantity1, check_money1, check_total_money1, check_quantity2, check_money2, check_total_money2, user_id)
values (#{orderId}, #{createTime}, #{orderStatus}, #{targetId}, #{goodsId}, #{goodsSkuId}, #{warehouseId}, #{inQuantity}, #{inMoney}, #{inTotalMoney}, #{outQuantity}, #{outMoney}, #{outTotalMoney}, #{checkQuantity}, #{checkMoney}, #{checkTotalMoney}, #{checkQuantity1}, #{checkMoney1}, #{checkTotalMoney1}, #{checkQuantity2}, #{checkMoney2}, #{checkTotalMoney2}, #{userId});

mapper.update:
update
    storage_check_order_${storeId}
set
    order_status = -1
where
    order_id = #{orderId}
and goods_sku_id = #{goodsSkuId}
and warehouse_id = #{warehouseId}
and order_status > 0
and in_quantity != 0
and in_money != 0
and in_total_money != 0;

update
    storage_check_order_${storeId}
set
    order_status = -1
where
    order_id = #{orderId}
and goods_sku_id = #{goodsSkuId}
and warehouse_id = #{warehouseId}
and order_status > 0
and out_quantity != 0
and out_money != 0
and out_total_money != 0;

