mapper.update:
update
    supplier_${storeId}
set
    name = #{name}, contacts = #{contacts}, contact_number = #{contactNumber}, contact_address = #{contactAddress}, fax = #{fax}, remark = #{remark}
where
    id = #{id};

update
    supplier_${storeId}
set
    advance_money = #{advanceMoney}
where
    id = #{id}
and advance_money = 0;

mapper.select:
select id, name, advance_money, contacts, contact_number, contact_address, fax, remark from supplier_${supplierVo.storeId} where 1 = 1
and name like concat('%', #{supplierVo.name}, '%')
limit #{pageVo.start}, #{pageVo.pageSize};
