mapper.select:
select count(0) from user_${storeId} as u, role_${storeId} as r, user_role_${storeId} as ur, warehouse_${storeId} as w where u.id = ur.user_id and r.id = ur.role_id and u.warehouse_id = w.id
and r.id = #{roleId}
and u.name like concat('%', #{name}, '%')

select u.id, u.name, u.username, u.password, u.phone, u.warehouse_id, u.disabled, u.remark, r.id as role_id, r.name as role_name, w.name as warehouse_name from user_${userVo.storeId} as u, role_${userVo.storeId} as r, user_role_${userVo.storeId} as ur, warehouse_${userVo.storeId} as w where u.id = ur.user_id and r.id = ur.role_id and u.warehouse_id = w.id
and r.id = #{userVo.roleId}
and u.name like concat('%', #{userVo.name}, '%')
limit #{pageVo.start}, #{pageVo.pageSize};

select u.id, u.name, u.username, u.password, u.phone, u.warehouse_id, u.disabled, u.remark, r.id as role_id, r.name as role_name, w.name as warehouse_name from user_${storeId} as u, role_${storeId} as r, user_role_${storeId} as ur, warehouse_${storeId} as w where u.id = ur.user_id and r.id = ur.role_id and u.warehouse_id = w.id
and r.id = #{roleId}
and u.name = #{name}
and u.warehouse_id = #{warehouseId}
