mapper.update:
update store
set name    = #{name},
    address = #{address}
where id = #{id};

update store
set sms_quantity = sms_quantity + #{smsQuantity}
where id = #{id};

update store
set sms_quantity = sms_quantity - #{smsQuantity}
where id = #{id}
  and sms_quantity - #{smsQuantity} >= 0;

mapper.select:
select count(0)
from store as s
         inner join client as c on c.id = s.client_id;
